import React, { useState } from 'react';

function App() {
  const [image, setImage] = useState(null);
  const [focalPoints, setFocalPoints] = useState([
    { x: 1000, y: 1000 },
    { x: 3000, y: 2000 },
    { x: 5000, y: 3000 }
  ]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    setImage(file);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);
    setLoading(true);

    console.log('Submitting image:', image);
    console.log('Focal points:', focalPoints);

    try {
      const formData = new FormData();
      formData.append('image', image);
      formData.append('focalPoints', JSON.stringify(focalPoints));

      for (let pair of formData.entries()) {
        console.log(pair[0], pair[1]);
      }

      const response = await fetch('http://localhost:5000/api/process_image', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to process image');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'wiggle.mp4';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>WiggleLens Image Processor</h1>
      
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Upload Image (6024x4024):
            <input 
              type="file" 
              accept="image/*" 
              onChange={handleImageUpload}
              required 
            />
          </label>
        </div>

        <button 
          type="submit" 
          disabled={!image || loading}
        >
          {loading ? 'Processing...' : 'Create Wiggle'}
        </button>
      </form>

      {error && (
        <div style={{ color: 'red', marginTop: '20px' }}>
          Error: {error}
        </div>
      )}
    </div>
  );
}

export default App;
