import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [image, setImage] = useState(null);
  const [fullImagePreview, setFullImagePreview] = useState(null);
  const [imagePreviews, setImagePreviews] = useState([]);
  const [imageSize, setImageSize] = useState({ width: 0, height: 0 });
  const [focalPoints, setFocalPoints] = useState([null, null, null]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [isSplit, setIsSplit] = useState(false);
  const [loadedImage, setLoadedImage] = useState(null);
  const [splitLoading, setSplitLoading] = useState(false);
  const [autoDetectLoading, setAutoDetectLoading] = useState(false);

  // NEW states for the next "options" screen
  const [showWiggleOptions, setShowWiggleOptions] = useState(false);
  const [frameSpeed, setFrameSpeed] = useState(0.1);  // 0.1 / 0.2 / 0.3
  const [videoLength, setVideoLength] = useState(5);  // 1..10 seconds slider
  // Add a state to control using the second frame as background
  const [useSecondFrameAsBackground, setUseSecondFrameAsBackground] = useState(false);

  // 1) Disable scrolling in website when at 100% zoom
  useEffect(() => {
    function checkZoom() {
      if (window.devicePixelRatio === 1) {
        // Hide scrollbars
        document.documentElement.style.overflow = 'hidden';
      } else {
        // Restore default scrolling
        document.documentElement.style.overflow = 'auto';
      }
    }
    checkZoom();

    // Re-check on window resize (user might zoom in/out)
    window.addEventListener('resize', checkZoom);
    return () => {
      window.removeEventListener('resize', checkZoom);
    };
  }, []);

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];

    // Create temporary image to check dimensions
    const img = new Image();
    img.onload = () => {
      // Check for exact 6000x4000 dimensions
      if (img.width !== 6000 || img.height !== 4000) {
        setError('Please upload an image that is exactly 6000x4000 pixels');
        // Reset file input
        e.target.value = '';
        return;
      }

      // If dimensions are correct, proceed with upload
      setImage(file);
      setFocalPoints([null, null, null]);
      setIsSplit(false);
      setError(null);  // Clear any previous errors

      setImageSize({ width: img.width, height: img.height });
      setFullImagePreview(img.src);
      setLoadedImage(img);

      // Ensure we go through the normal steps again, not straight to final options
      setShowWiggleOptions(false);
    };

    img.onerror = () => {
      setError('Failed to load image');
      e.target.value = '';
    };

    img.src = URL.createObjectURL(file);
  };

  const handleSplitImage = async () => {
    // Reset focal points every time user re-splits the image.
    setFocalPoints([null, null, null]);

    setSplitLoading(true);
    try {
      const img = loadedImage;
      const sectionWidth = Math.floor(img.width / 3);

      // Create sections in parallel
      const sectionPromises = [0, 1, 2].map(async (i) => {
        const canvas = new OffscreenCanvas(sectionWidth, img.height);
        const ctx = canvas.getContext('2d');

        // Draw section
        ctx.drawImage(
          img,
          i * sectionWidth, 0, sectionWidth, img.height,
          0, 0, sectionWidth, img.height
        );

        // Convert to blob and then to data URL
        const blob = await canvas.convertToBlob({ type: 'image/jpeg', quality: 0.8 });
        return URL.createObjectURL(blob);
      });

      const previews = await Promise.all(sectionPromises);
      setImagePreviews(previews);
      setIsSplit(true);
    } catch (err) {
      console.error('Error splitting image:', err);
      setError('Failed to split image');
    } finally {
      setSplitLoading(false);
    }
  };

  const handleSectionClick = (sectionIndex, e) => {
    const rect = e.target.getBoundingClientRect();
    const image = e.target;

    // Get the actual rendered size of the image
    const renderedWidth = image.width;
    const renderedHeight = image.height;

    // Calculate the scaling factor between rendered size and natural size
    const scaleX = image.naturalWidth / renderedWidth;
    const scaleY = image.naturalHeight / renderedHeight;

    // Get click position relative to the rendered image
    // Account for potential padding due to object-fit: contain
    const imageAspect = image.naturalWidth / image.naturalHeight;
    const containerAspect = renderedWidth / renderedHeight;

    let x = e.clientX - rect.left;
    let y = e.clientY - rect.top;

    // Adjust coordinates if image is letterboxed due to object-fit: contain
    if (imageAspect > containerAspect) {
      // Image is wider than container - has vertical padding
      const actualHeight = renderedWidth / imageAspect;
      const padding = (renderedHeight - actualHeight) / 2;
      y = y - padding;
      // Clamp y to actual image bounds
      y = Math.max(0, Math.min(y, actualHeight));
    } else {
      // Image is taller than container - has horizontal padding
      const actualWidth = renderedHeight * imageAspect;
      const padding = (renderedWidth - actualWidth) / 2;
      x = x - padding;
      // Clamp x to actual image bounds
      x = Math.max(0, Math.min(x, actualWidth));
    }

    // Scale to original image coordinates
    const originalX = Math.round(x * scaleX);
    const originalY = Math.round(y * scaleY);

    // Add the section offset
    const sectionWidth = imageSize.width / 3;
    const adjustedX = originalX + (sectionIndex * sectionWidth);

    // Clamp values to ensure they stay within the section bounds
    const finalX = Math.max(
      sectionIndex * sectionWidth,
      Math.min(adjustedX, (sectionIndex + 1) * sectionWidth - 1)
    );
    const finalY = Math.max(0, Math.min(originalY, imageSize.height - 1));

    const newFocalPoints = [...focalPoints];
    newFocalPoints[sectionIndex] = { x: finalX, y: finalY };
    setFocalPoints(newFocalPoints);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (focalPoints.some(fp => fp === null)) {
      setError('Please set a focal point on each section before creating wiggle.');
      return;
    }
    setError(null);
    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('image', image);
      formData.append('focalPoints', JSON.stringify(focalPoints));
      formData.append('frameSpeed', frameSpeed);
      formData.append('videoLength', videoLength);
      // Pass along our new option: "useSecondFrameAsBackground"
      formData.append('useSecondFrameAsBackground', useSecondFrameAsBackground.toString());

      const response = await fetch('http://localhost:5000/api/process_image_no_rgba', {
        method: 'POST',
        body: formData,
      });
      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to process image');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'wiggle.mp4';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  // Show the Wiggle Options panel, hide the upload form & 3 images
  function handleNext() {
    setShowWiggleOptions(true);
  }

  const handleBack = () => {
    // If we're currently in the wiggle options form, just go back to isSplit (the prior form)
    if (showWiggleOptions) {
      setShowWiggleOptions(false);
      return;
    }

    // Otherwise, proceed with the existing logic:
    if (isSplit) {
      setIsSplit(false);
    } else if (image) {
      // If we haven't split yet, go back to upload
      setImage(null);
      setFullImagePreview(null);
      setImagePreviews([]);
      setFocalPoints([null, null, null]);
    }
  };

  function handleRotate90() {
    if (!loadedImage) return;

    // Create an offscreen canvas to draw the rotated image
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    // The rotated image dimensions swap: width -> height, height -> width
    canvas.width = loadedImage.height;
    canvas.height = loadedImage.width;

    // Move the origin to the center of the canvas, rotate 90 deg clockwise, then draw
    ctx.translate(canvas.width / 2, canvas.height / 2);
    ctx.rotate(Math.PI / 2); 
    // Draw the image so its center is at the canvas origin
    ctx.drawImage(
      loadedImage, 
      -loadedImage.width / 2, 
      -loadedImage.height / 2
    );

    // Convert canvas back to data URL and load it into an <img> so we can update loadedImage
    const rotatedUrl = canvas.toDataURL('image/jpeg');
    const rotatedImg = new Image();
    rotatedImg.onload = () => {
      // Update states for new (rotated) image
      setLoadedImage(rotatedImg);
      setFullImagePreview(rotatedImg.src);
      // Also update imageSize so that focal points compute correctly
      setImageSize({ width: rotatedImg.width, height: rotatedImg.height });
    };
    rotatedImg.src = rotatedUrl;
  }

  return (
    <div className="container">
      {(image || isSplit) && (
        <button 
          className="back-arrow" 
          onClick={handleBack}
          title="Go back"
        >
          ←
        </button>
      )}

      {/*
       * Only show this layout when no image is uploaded and we haven't split.
       * We build a 2-column layout: left text, right upload button.
       */}
      {!image && !isSplit && (
        <div
          style={{
            display: 'flex',
            width: '100%',
            justifyContent: 'center',
            alignItems: 'center',
            padding: '40px 20px',
            boxSizing: 'border-box',
          }}
        >
          {/* Left 50%: Title, subtitle, description */}
          <div 
            style={{
              width: '50%',
              textAlign: 'center',
            }}
          >
            <h1
              style={{
                fontSize: '6em',
                marginBottom: '10px',
                color: '#5e645a',
                fontWeight: 'bold',
                marginTop: '20%'
              }}
            >
              DispoStudio
            </h1>
            <h2
              style={{
                fontSize: '2.5em',
                marginBottom: '10px',
                marginTop: '10px',
                color: '#5e645a',
                fontWeight: 'normal',
              }}
            >
              Wiggle Lens Maker
            </h2>
            <p
              style={{
                lineHeight: '1.5',
                color: '#5e645a',
                marginTop: '20%',
                opacity: '60%',
                fontSize: '28px',
                margin: '20% 20%'
              }}
            >
              Easily create wiggle lens effect videos with your 
              <a 
                href="https://www.etsy.com/uk/shop/DispoStudio"
                target="_blank"
                rel="noopener noreferrer"
                style={{ color: '#5e645a', textDecoration: 'underline', marginLeft: '5px', marginRight: '5px' }}
              >
                Swiss Army Lens
              </a> 
              photos in just a few clicks.
            </p>
          </div>

          {/* Right 50%: Upload button */}
          <div style={{ width: '50%' }}>
            <div className="upload-container" style={{ textAlign: 'center' }}>
              <label className="upload-label">
                <input 
                  type="file" 
                  accept="image/*" 
                  onChange={handleImageUpload}
                  required 
                  className="upload-input"
                />
                Upload Image (6000x4000)
              </label>
            </div>
          </div>
        </div>
      )}

      {/* Instead of always rendering the entire form with the images,
          conditionally hide the form's content if showWiggleOptions is true */}
      {!showWiggleOptions && (
        <form onSubmit={handleSubmit} className="upload-form">
          {fullImagePreview && !isSplit && (
            <div className="full-image-container" style={{ display: 'flex', gap: '20px' }}>
              <img 
                src={fullImagePreview} 
                alt="Full uploaded image" 
                className="full-image"
              />
              <div
                style={{
                  // Make this container 90% wide & centered
                  display: 'flex',
                  flexDirection: 'row',
                  alignItems: 'center',
                  justifyContent: 'space-between',
                  width: '90%',
                  margin: '0 auto',
                }}
              >
                <button 
                  type="button"
                  onClick={handleRotate90}
                  className="rotate-button"
                  style={{
                    width: '45%',         // half of container (minus gap)
                    textAlign: 'center',
                    backgroundColor: '#5e645a',
                  }}
                >
                  Rotate 90°
                </button>

                <button 
                  type="button"  
                  onClick={handleSplitImage}
                  className="split-button"
                  disabled={splitLoading}
                  style={{
                    width: '45%',         // half of container (minus gap)
                    textAlign: 'center',
                    backgroundColor: '#5e645a',
                  }}
                >
                  {splitLoading ? 'Splitting...' : 'Split Image'}
                </button>
              </div>
            </div>
          )}

          {isSplit && imagePreviews.length > 0 && (
            <>
              <div className="sections-container">
                {imagePreviews.map((preview, index) => (
                  <div key={index} className="section-card">
                    <div
                      className="section-image-container"
                    >
                      <img
                        src={preview}
                        alt={`Section ${index + 1}`}
                        onClick={(e) => handleSectionClick(index, e)}
                        className="section-image"
                      />
                      {focalPoints[index] && (
                        <div
                          className="focal-marker"
                          style={{
                            left: `${(
                              (focalPoints[index].x - index * (imageSize.width / 3)) 
                              / (imageSize.width / 3)
                            ) * 100}%`,
                            top: `${(focalPoints[index].y / imageSize.height) * 100}%`,
                          }}
                        />
                      )}
                    </div>

                    {focalPoints[index] && (
                      <div className="coordinates-text">
                        {`Focal point: (${Math.round(
                          focalPoints[index].x - index * (imageSize.width / 3)
                        )}, ${Math.round(focalPoints[index].y)})`}
                      </div>
                    )}
                  </div>
                ))}
              </div>
              {/* Show this prompt only if at least one focal point is still null */}
              {focalPoints.some(fp => fp === null) && (
                <h3 className="previews-title">Click on each section to set focal points:</h3>
              )}
            </>
          )}

          {/* If all 3 focal points are set, show "Next" instead of "Create Wiggle" */}
          {isSplit && !focalPoints.some(fp => fp === null) && (
            <button
              type="button"
              className="submit-button"
              onClick={handleNext}
            >
              Next
            </button>
          )}
        </form>
      )}

      {/* If user clicked "Next," show the new panel (no more 3 images or upload form) */}
      {showWiggleOptions && (
        <form onSubmit={handleSubmit} className="upload-form" style={{ marginTop: '5%', textAlign: 'center' }}>
          <div style={{ marginBottom: '40px', marginTop: '40px', textAlign: 'center', width: '100%' }}>
            <button
              type="button"
              disabled
              className="split-button"
              style={{
                backgroundColor: '#5e645a',
                cursor: 'default',
                marginBottom: '20px',
                margin: '0 30px',
                width: '25%'
              }}
            >
              Frame Speed:
            </button>

            {/* Fast: 0.1s */}
            <div style={{ display: 'inline-block', margin: '0 20px', textAlign: 'center', width: '20%' }}>
              <h3 style={{ margin: '0 0 5px 0', color: '#5e645a' }}>0.1s</h3>
              <button
                type="button"
                className={`frame-button ${frameSpeed === 0.1 ? 'selected-frame' : ''}`}
                onClick={() => setFrameSpeed(0.1)}
              >
                Fast
              </button>
            </div>

            {/* Medium: 0.2s */}
            <div style={{ display: 'inline-block', margin: '0 20px', textAlign: 'center', width: '20%' }}>
              <h3 style={{ margin: '0 0 5px 0', color: '#5e645a' }}>0.2s</h3>
              <button
                type="button"
                className={`frame-button ${frameSpeed === 0.2 ? 'selected-frame' : ''}`}
                onClick={() => setFrameSpeed(0.2)}
              >
                Medium
              </button>
            </div>

            {/* Slow: 0.3s */}
            <div style={{ display: 'inline-block', margin: '0 20px', textAlign: 'center', width: '20%' }}>
              <h3 style={{ margin: '0 0 5px 0', color: '#5e645a' }}>0.3s</h3>
              <button
                type="button"
                className={`frame-button ${frameSpeed === 0.3 ? 'selected-frame' : ''}`}
                onClick={() => setFrameSpeed(0.3)}
              >
                Slow
              </button>
            </div>
          </div>

          {/* Video Length slider: 1..10 seconds */}
          <div style={{ marginBottom: '40px', marginTop: '40px', textAlign: 'center', width: '100%' }}>
            <button
              type="button"
              disabled
              className="split-button"
              style={{
                backgroundColor: '#5e645a',
                cursor: 'default',
                marginBottom: '20px',
                margin: '0 30px',
                width: '30%',
              }}
            >
              Video Length: {parseFloat(videoLength).toPrecision(2)}s
            </button>
            <input
              type="range"
              min="1"
              max="10"
              value={videoLength}
              onChange={(e) => setVideoLength(parseFloat(e.target.value))}
              step={(3 * frameSpeed).toPrecision(1)}
              className="video-slider"
              defaultValue={5}
            />
          </div>

          {/* Choose background mode */}
          <div style={{ margin: '30px 0', textAlign: 'center' }}>
            <h3 style={{ color: '#5e645a', marginBottom: '10px' }}>Background:</h3>
            <button
              type="button"
              className="split-button"
              style={{ marginRight: '20px' }}
              onClick={() => setUseSecondFrameAsBackground(true)}
            >
              Use 2nd Frame BG
            </button>
            <button
              type="button"
              className="split-button"
              onClick={() => setUseSecondFrameAsBackground(false)}
            >
              Black BG
            </button>
          </div>

          {/* Now the real "Create Wiggle" button */}
          <button
            type="submit"
            className="submit-button"
          >
            {loading ? 'Processing...' : 'Create Wiggle'}
          </button>
        </form>
      )}

      {error && (
        <div className="error-message">
          Error: {error}
        </div>
      )}
    </div>
  );
}

export default App;
