import React, { useState } from 'react';

function App() {
  const [image, setImage] = useState(null);
  const [imagePreviews, setImagePreviews] = useState([]);
  const [imageSize, setImageSize] = useState({ width: 0, height: 0 });
  const [focalPoints, setFocalPoints] = useState([
    null, null, null  // Initialize as null to indicate unset focal points
  ]);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageUpload = async (e) => {
    const file = e.target.files[0];
    setImage(file);
    setFocalPoints([null, null, null]); // Reset focal points for new image

    // Create preview of the three sections
    const img = new Image();
    img.onload = () => {
      setImageSize({ width: img.width, height: img.height });
      const canvas = document.createElement('canvas');
      const ctx = canvas.getContext('2d');
      
      canvas.width = img.width;
      canvas.height = img.height;
      ctx.drawImage(img, 0, 0);
      
      const sectionWidth = Math.floor(img.width / 3);
      
      const previews = [];
      for (let i = 0; i < 3; i++) {
        const sectionCanvas = document.createElement('canvas');
        sectionCanvas.width = sectionWidth;
        sectionCanvas.height = img.height;
        const sectionCtx = sectionCanvas.getContext('2d');
        
        sectionCtx.drawImage(
          img,
          i * sectionWidth, 0, sectionWidth, img.height,
          0, 0, sectionWidth, img.height
        );
        
        previews.push(sectionCanvas.toDataURL());
      }
      
      setImagePreviews(previews);
    };
    
    img.src = URL.createObjectURL(file);
  };

  const handleSectionClick = (sectionIndex, e) => {
    const rect = e.target.getBoundingClientRect();
    const x = e.clientX - rect.left;
    const y = e.clientY - rect.top;
    
    // Convert clicked coordinates to original image scale
    const sectionWidth = imageSize.width / 3;
    const scaleX = sectionWidth / e.target.width;
    const scaleY = imageSize.height / e.target.height;
    
    const originalX = Math.round(x * scaleX) + (sectionIndex * sectionWidth);
    const originalY = Math.round(y * scaleY);
    
    const newFocalPoints = [...focalPoints];
    newFocalPoints[sectionIndex] = { x: originalX, y: originalY };
    setFocalPoints(newFocalPoints);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Check if all focal points are set
    if (focalPoints.some(fp => fp === null)) {
      setError('Please set focal points for all sections by clicking on them');
      return;
    }

    setError(null);
    setLoading(true);

    try {
      const formData = new FormData();
      formData.append('image', image);
      formData.append('focalPoints', JSON.stringify(focalPoints));

      const response = await fetch('http://localhost:5000/api/process_image', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to process image');
      }

      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = 'wiggle.mp4';
      document.body.appendChild(a);
      a.click();
      document.body.removeChild(a);
      window.URL.revokeObjectURL(url);

    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h1>WiggleLens Image Processor</h1>
      
      <form onSubmit={handleSubmit}>
        <div>
          <label>
            Upload Image (6024x4024):
            <input 
              type="file" 
              accept="image/*" 
              onChange={handleImageUpload}
              required 
            />
          </label>
        </div>

        {imagePreviews.length > 0 && (
          <div style={{ marginTop: '20px' }}>
            <h3>Click on each section to set focal points:</h3>
            <div style={{ display: 'flex', gap: '10px', overflowX: 'auto' }}>
              {imagePreviews.map((preview, index) => (
                <div key={index} style={{ flex: '0 0 auto', position: 'relative' }}>
                  <h4>Section {index + 1}</h4>
                  <img 
                    src={preview} 
                    alt={`Section ${index + 1}`} 
                    onClick={(e) => handleSectionClick(index, e)}
                    style={{ 
                      maxWidth: '300px', 
                      maxHeight: '400px',
                      objectFit: 'contain',
                      border: '1px solid #ccc',
                      cursor: 'crosshair'
                    }} 
                  />
                  {focalPoints[index] && (
                    <div 
                      style={{
                        position: 'absolute',
                        width: '10px',
                        height: '10px',
                        borderRadius: '50%',
                        backgroundColor: 'red',
                        transform: 'translate(-50%, -50%)',
                        pointerEvents: 'none',
                        left: `${(focalPoints[index].x % (imageSize.width / 3)) / (imageSize.width / 3) * 100}%`,
                        top: `${focalPoints[index].y / imageSize.height * 100}%`
                      }}
                    />
                  )}
                  <div style={{ textAlign: 'center', marginTop: '5px' }}>
                    {focalPoints[index] ? 
                      `Focal point set at (${focalPoints[index].x}, ${focalPoints[index].y})` : 
                      'Click to set focal point'}
                  </div>
                </div>
              ))}
            </div>
          </div>
        )}

        <button 
          type="submit" 
          disabled={!image || loading || focalPoints.some(fp => fp === null)}
          style={{ marginTop: '20px' }}
        >
          {loading ? 'Processing...' : 'Create Wiggle'}
        </button>
      </form>

      {error && (
        <div style={{ color: 'red', marginTop: '20px' }}>
          Error: {error}
        </div>
      )}
    </div>
  );
}

export default App;
